#include <stdint.h>
#include <stdbool.h>
#include "stm32f103xb.h"
#include "init.h"
#include "buttons.h"

void control_buttons(void);

uint8_t turn_left = 0;
uint8_t turn_right = 0;
uint16_t delay_cnt = 0;
uint8_t flash_cnt = 0;


void TIM1_UP_IRQHandler (void)
{
	TIM1->SR = 0;

	buttons_process();

	if (turn_left && flash_cnt < 20)
	{
		delay_cnt++;
		if (delay_cnt >= 200)
		{
			LEFT_LIGHT_SWITCH;
			delay_cnt = 0;
			flash_cnt++;
		}
		RIGHT_LIGHT_RESET;
	}
	else if (turn_right && flash_cnt < 20)
	{
		delay_cnt++;
		if (delay_cnt >= 200)
		{
			RIGHT_LIGHT_SWITCH;
			delay_cnt = 0;
			flash_cnt++;
		}
		LEFT_LIGHT_RESET;
	}
	else
	{
		turn_left = turn_right = 0;
		delay_cnt = 0;
		flash_cnt = 0;
		LEFT_LIGHT_RESET;
		RIGHT_LIGHT_RESET;
	}
}

int main(void)
{
    init_mcu();
    while (1)
    {
    	control_buttons();
    }
}

void control_buttons(void)
{
	for (int i = 0; i < 3; i++)
	{
		button_t* button = &buttons[i];

		if (button->push_unhandled == true)
		{
			button->push_unhandled = false;
			switch (i)
			{
				case LEFT_BUTTON:
					turn_left ^= 1;
					turn_right = 0;
					flash_cnt = 0;
					break;
				case MIDDLE_BUTTON:
					MIDDLE_LIGHT_SWITCH;
					break;
				case RIGHT_BUTTON:
					turn_right ^= 1;
					turn_left = 0;
					flash_cnt = 0;
					break;
				default:
					break;
			}
		}
	}
}
